import React, { createContext, useContext, useState, useEffect } from 'react';
import { message } from 'antd';
import api from '../utils/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [adminInfo, setAdminInfo] = useState(null);

  // æ£€æŸ¥æœ¬åœ°å­˜å‚¨çš„token
  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('admin_token');
      if (token) {
        try {
          // éªŒè¯tokenæœ‰æ•ˆæ€§
          const response = await api.get('/admin/profile');
          if (response.data.success) {
            setIsAuthenticated(true);
            setAdminInfo(response.data.data);
          } else {
            localStorage.removeItem('admin_token');
          }
        } catch (error) {
          console.error('TokenéªŒè¯å¤±è´¥:', error);
          localStorage.removeItem('admin_token');
        }
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  const login = async (password) => {
    try {
      console.log('ðŸš€ å¼€å§‹ç™»å½•è¯·æ±‚...', { password });
      const response = await api.post('/admin/login', { password });

      console.log('ðŸ“„ ç™»å½•å“åº”:', response.data);

      // æ£€æŸ¥å“åº”æ•°æ®ç»“æž„
      if (response.data && (response.data.success || response.data.code === 200)) {
        const responseData = response.data.data || response.data;
        const { token, permissions, expireTime } = responseData;

        if (token) {
          // ä¿å­˜tokenå’Œç”¨æˆ·ä¿¡æ¯
          localStorage.setItem('admin_token', token);
          localStorage.setItem('admin_permissions', JSON.stringify(permissions || []));
          localStorage.setItem('admin_expire_time', expireTime || '');

          setIsAuthenticated(true);
          setAdminInfo({
            username: 'admin',
            permissions: permissions || [],
            expireTime
          });

          message.success('ç™»å½•æˆåŠŸ');
          return { success: true };
        } else {
          message.error('æœåŠ¡å™¨å“åº”æ•°æ®å¼‚å¸¸');
          return { success: false, message: 'æœåŠ¡å™¨å“åº”æ•°æ®å¼‚å¸¸' };
        }
      } else {
        const errorMsg = response.data?.message || 'ç™»å½•å¤±è´¥';
        message.error(errorMsg);
        return { success: false, message: errorMsg };
      }
    } catch (error) {
      console.error('ðŸš¨ ç™»å½•é”™è¯¯:', error);

      let errorMessage = 'ç™»å½•å¤±è´¥ï¼Œè¯·æ£€æŸ¥ç½‘ç»œè¿žæŽ¥';

      if (error.response) {
        // æœåŠ¡å™¨å“åº”äº†é”™è¯¯çŠ¶æ€ç 
        console.error('æœåŠ¡å™¨é”™è¯¯:', error.response.status, error.response.data);
        errorMessage = error.response.data?.message || `æœåŠ¡å™¨é”™è¯¯ (${error.response.status})`;
      } else if (error.request) {
        // è¯·æ±‚å·²å‘é€ä½†æ²¡æœ‰å“åº”
        console.error('ç½‘ç»œé”™è¯¯:', error.request);
        errorMessage = 'ç½‘ç»œè¿žæŽ¥å¤±è´¥ï¼Œè¯·æ£€æŸ¥åŽç«¯æœåŠ¡å™¨æ˜¯å¦å¯åŠ¨';
      } else {
        // è¯·æ±‚è®¾ç½®æ—¶å‘ç”Ÿé”™è¯¯
        console.error('è¯·æ±‚é…ç½®é”™è¯¯:', error.message);
        errorMessage = 'è¯·æ±‚é…ç½®é”™è¯¯';
      }

      message.error(errorMessage);
      return { success: false, message: errorMessage };
    }
  };

  const logout = async () => {
    try {
      await api.post('/admin/logout');
    } catch (error) {
      console.error('ç™»å‡ºè¯·æ±‚å¤±è´¥:', error);
    } finally {
      // æ¸…é™¤æœ¬åœ°å­˜å‚¨
      localStorage.removeItem('admin_token');
      localStorage.removeItem('admin_permissions');
      localStorage.removeItem('admin_expire_time');

      setIsAuthenticated(false);
      setAdminInfo(null);
      message.success('å·²é€€å‡ºç™»å½•');
    }
  };

  const hasPermission = (permission) => {
    return adminInfo?.permissions?.includes(permission) || false;
  };

  const value = {
    isAuthenticated,
    loading,
    adminInfo,
    login,
    logout,
    hasPermission
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
